import React, { useEffect, useState } from 'react'
import './App.css'
import { ConnectButton, Modal } from 'web3uikit'
import logo from './images/Moralis.png'
import Coin from './components/Coin'

const App = () => {
  const [btc, setBtc] = useState(38)
  const [eth, setEth] = useState(38)
  const [link, set] = useState(38)
  const [visible, setLink] = useState(false)
  const [modalToken, setModalToken] = useState(false)

  return (
    <>
      <div className="header">
        <div className="logo">
          <img src={logo} alt="logo" height="50px" />
          Sentiment
        </div>
        <ConnectButton />
      </div>
      <div className="instructions">
        Where do you think these tokens are going? Up or Down?
      </div>

      <div className="list">
        <Coin
          perc={btc}
          setPerc={setBtc}
          token={'BTC'}
          setModalToken={setModalToken}
          setVisible={setVisible}
        />
        <Coin
          perc={eth}
          setPerc={setBtc}
          token={'ETH'}
          setModalToken={setModalToken}
          setVisible={setVisible}
        />
        <Coin
          perc={link}
          setPerc={setBtc}
          token={'LINK'}
          setModalToken={setModalToken}
          setVisible={setVisible}
        />
      </div>

      <Modal
        isVisible={visible}
        onCloseButtonPressed={() => {
          setVisible(false)
        }}
        hasFooter={false}
        title={modalToken}
      ></Modal>
    </>
  )
}

export default App
